		,salary=#{salary}
		,opendate=#{opendate}
		,closedate=#{closedate}
		,graduation=#{graduation}
		,content=#{content}
		
			
				,ph_d_candidate=#{ph_d_candidate}
			
			
				,ph_d_candidate=null
			
		

	
		where
		g_no=#{g_no}

		update
